package day15;

public class NumberToChar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int x = 100;
		char myChar = (char) x;
		
		System.out.println(myChar); // it will show ASCII of the  value 
	
		
		
		//special--->UpperCase---->LoweCase
	
		double y = 100.99;
		char myChar2 = (char)y;
		System.out.println(myChar2); //it will still show ASCII of the value 
		
		
		
		
		
		
	}

}
